import { Button, Col, Container, Row } from "react-bootstrap";

function App() {
  const person=[
    // {id:"1" , name:"هادي" , date:"5مساء" , img:"per.jpg"},
    // {id:"2" , name:"احمد" , date:"9مساء" , img:"per.jpg"},
    // {id:"3" , name:"محمود" , date:"2مساء" , img:"per.jpg"},
    // {id:"4" , name:"حسن" , date:"6مساء" , img:"per.jpg"},
    // {id:"5" , name:"مصطفي" , date:"4مساء" , img:"per.jpg"},
  ];
  return (
    <div className="font color-body">
      <Container className="py-5">
        <Row className="justify-content-center my-2">
          <Col sm="8" className="">
            لديك {person.length} مواعيد اليوم
          </Col>
        </Row>
        <Row className="justify-content-center my-2">
          <Col sm="8" className="">
            <div className="rectangle p-2" >
             {person.length? person.map((item)=>{
              return(
                <div className="d-flex border-bottom mx-2 my-2">
                <img alt="mmm" src="per.jpg" className="img-avatar" />
                <div className="px-3">
                  <p className="d-inline fs-5"> {item.name}</p>
                  <p className="fs-6">{item.date}</p>
                </div>
              </div>
              )
             }) :<h2 className="p-5">لا يوجد بيانات</h2>}
            </div>
          </Col>
        </Row>
        <Row className="justify-content-center my-2">
          <Col sm="8" className="d-flex justify-content-between">
            <button className="btn-style p-2">مسح الكل</button>
            <button className="btn-style p-2">عرض البيانات</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
